import {
  FETCH_FRIENDS,
  FETCH_FRIENDS_SUCCESS,
  FETCH_FRIENDS_FAILURE,
  ADD_FRIEND_START,
  ADD_FRIEND_SUCCESS,
  ADD_FRIEND_FAILURE,
  UPDATE_FRIEND_START,
  UPDATE_FRIEND_SUCCESS,
  UPDATE_FRIEND_FAILURE,
  DELETE_FRIEND_START,
  DELETE_FRIEND_SUCCESS,
  DELETE_FRIEND_FAILURE
} from "../actions";

const initialState = {
  friends: [],
  isFetching: false,
  error: null
};

const friendsReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_FRIENDS:
      return {
        ...state,
        isFetching: true
      };

    case FETCH_FRIENDS_SUCCESS:
      return {
        ...state,
        error: null,
        isFetching: false,
        friends: action.payload
      };

    case FETCH_FRIENDS_FAILURE:
      return {
        ...state,
        isFetching: false,
        error: action.payload
      };

    // Add New Friend //

    case ADD_FRIEND_START:
      return {
        ...state,
        isFetching: true
      };

    case ADD_FRIEND_SUCCESS:
      return {
        ...state,
        error: null,
        isFetching: false,
        friends: action.payload
      };

    case ADD_FRIEND_FAILURE:
      return {
        ...state,
        isFetching: false,
        error: action.payload
      };

    // Update Friend //

    case UPDATE_FRIEND_START:
      return {
        ...state,
        isFetching: true
      };

    case UPDATE_FRIEND_SUCCESS:
      return {
        ...state,
        error: null,
        isFetching: false,
        friends: action.payload
      };

    case UPDATE_FRIEND_FAILURE:
      return {
        ...state,
        isFetching: false,
        error: action.payload
      };

    // Delete Friend //

    case DELETE_FRIEND_START:
      return {
        ...state,
        isFetching: true
      };

    case DELETE_FRIEND_SUCCESS:
      return {
        ...state,
        error: null,
        isFetching: false,
        friends: action.payload
      };

    case DELETE_FRIEND_FAILURE:
      return {
        ...state,
        isFetching: false,
        error: action.payload
      };

    default:
      return state;
  }
};

export default friendsReducer;
